import { useEffect, useState } from 'react';
import { getMessages, sendMessage } from '../api/messages';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import '../styles/chatwindow.css';

function ChatWindow({ chatId, chatName, user }) {
    const [messages, setMessages] = useState([]);
    const [text, setText] = useState('');

    useEffect(() => {
        if (chatId && user) {
            getMessages(chatId)
                .then(setMessages)
                .catch((err) => {
                    toast.error('Failed to fetch messages');
                    console.error(err);
                });
        } else {
            // Clear messages if no chatId or user logged out
            setMessages([]);
        }
    }, [chatId, user]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!user) {
            toast.warn('You must be logged in to send messages.');
            return;
        }

        const trimmed = text.trim();
        if (!trimmed) return;

        try {
            await sendMessage(chatId, trimmed);
            toast.success('Message sent!');
            setText('');
            getMessages(chatId).then(setMessages);

            setTimeout(() => {
                getMessages(chatId).then(setMessages);
            }, 3500);
        } catch (err) {
            toast.error('Failed to send message');
            console.error(err);
        }
    };

    if (!user) {
        return (
            <div className="chat-container">
                <p className="chat-locked-message">ðŸ”’ Please log in to view or send messages.</p>
                <ToastContainer />
            </div>
        );
    }

    return (
        <div className="chat-container">
            <div className="chat-messages">
                {messages.map((message, i) => (
                    <div
                        key={i}
                        className={`chat-message-wrapper ${message.sender === 'bot' ? 'bot' : 'user'}`}
                    >
                        <div className={`chat-message ${message.sender}`}>
                            <div className="message-text">{message.text}</div>
                        </div>
                        <div className={`message-time ${message.sender}`}>
                            {new Date(message.createdAt).toLocaleString('en-US', {
                                year: 'numeric',
                                month: 'short',
                                day: 'numeric',
                                hour: '2-digit',
                                minute: '2-digit',
                            })}
                        </div>
                    </div>
                ))}
            </div>

            <form onSubmit={handleSubmit} className="chat-input-area">
                <input
                    value={text}
                    onChange={(e) => setText(e.target.value)}
                    placeholder="Type your message..."
                />
                <button type="submit">Send</button>
            </form>

            <ToastContainer />
        </div>
    );
}

export default ChatWindow;
