        <div className="app">
            {/* ðŸ”¹ ÐŸÐµÑ€ÐµÐ´Ð°Ñ”Ð¼Ð¾ user Ñ– setUser Ñƒ Sidebar */}
            <Sidebar onSelect={setSelectedChat} user={user} setUser={setUser} />

            <main className="chat-window">
                {selectedChat ? (
