import { useState, useEffect } from 'react';
import { getChats, createChat, updateChat, deleteChat } from '../api/chats';
import { getMessages } from '../api/messages';
import { auth, provider } from '../firebase';
import { signInWithPopup } from 'firebase/auth';
import './Sidebar.css';

function Sidebar({ onSelect }) {
  const [chats, setChats] = useState([]);
  const [editingId, setEditingId] = useState(null);
  const [editFirstName, setEditFirstName] = useState('');
  const [editLastName, setEditLastName] = useState('');
  const [search, setSearch] = useState('');
  const [user, setUser] = useState(null);
  const [lastMessages, setLastMessages] = useState({});

  useEffect(() => {
    const fetchChatsAndLastMessages = async () => {
      const chatList = await getChats();
      setChats(chatList);

      const messagesMap = {};
      for (const chat of chatList) {
        const msgs = await getMessages(chat._id);
        if (msgs.length > 0) {
          const last = msgs[msgs.length - 1];
          messagesMap[chat._id] = {
            text: last.text,
            date: new Date(last.createdAt).toLocaleDateString('en-US', {
              month: 'short',
              day: 'numeric',
              year: 'numeric',
            }),
          };
        }
      }
      setLastMessages(messagesMap);
    };

    fetchChatsAndLastMessages();
  }, []);

  const handleLogin = async () => {
    try {
      const result = await signInWithPopup(auth, provider);
      setUser(result.user);
    } catch (err) {
      console.error('Login failed:', err);
    }
  };

  const handleCreateChatFromSearch = async () => {
    if (!search.trim()) return;
    const [first, ...rest] = search.trim().split(' ');
    const last = rest.join(' ') || ' ';
    await createChat(first, last);
    const updatedChats = await getChats();
    setChats(updatedChats);
    const created = updatedChats.find(c => c.firstName === first && c.lastName === last);
    if (created) onSelect(created);
    setSearch('');
  };

  const handleUpdate = async (id) => {
    await updateChat(id, editFirstName, editLastName);
    setEditingId(null);
    setChats(await getChats());
  };

  const handleDelete = async (id) => {
    await deleteChat(id);
    setChats(prev => prev.filter(chat => chat._id !== id));
  };

  const filteredChats = chats.filter(chat =>
    `${chat.firstName} ${chat.lastName}`.toLowerCase().includes(search.toLowerCase())
  );

  const chatExists = filteredChats.length > 0;

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && !chatExists) {
      handleCreateChatFromSearch();
    }
  };

  return (
    <div className="sidebar">
      <div className="sidebar-top">
        <div className="sidebar-login">
          {user ? (
            <img src={user.photoURL} alt="avatar" />
          ) : (
            <button onClick={handleLogin}>Log in</button>
          )}
        </div>
        <div className="search-container">
          <input
            className="search-input"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            onKeyDown={handleKeyDown}
            placeholder="Search or start new chat"
          />
          {!chatExists && search.trim() && (
            <button className="create-inline" onClick={handleCreateChatFromSearch}>Create</button>
          )}
        </div>
      </div>

      <h4 className="sidebar-title">Chats</h4>

      <ul className="chat-list">
        {filteredChats.map((chat) => {
          const last = lastMessages[chat._id];
          return (
            <li key={chat._id} className="chat-item">
              {editingId === chat._id ? (
                <div className="edit-mode">
                  <div className="input-block">
                    <label className="edit-label" htmlFor="first-name">First Name</label>
                    <input
                      id="first-name"
                      value={editFirstName}
                      onChange={(e) => setEditFirstName(e.target.value)}
                    />
                  </div>
                  <div className="input-block">
                    <label className="edit-label" htmlFor="last-name">Last Name</label>
                    <input
                      id="last-name"
                      value={editLastName}
                      onChange={(e) => setEditLastName(e.target.value)}
                    />
                  </div>
                  <div className="edit-buttons">
                    <button onClick={() => handleUpdate(chat._id)}>💾</button>
                    <button onClick={() => setEditingId(null)}>❌</button>
                  </div>
                </div>
              ) : (
                <div className="chat-info" onClick={() => onSelect(chat)}>
                  <div className="chat-name-row">
                    <span className="chat-name">{chat.firstName} {chat.lastName}</span>
                    {last && <span className="chat-date">{last.date}</span>}
                  </div>
                  {last && <div className="chat-preview">{last.text}</div>}
                  <div className="chat-actions">
                    <button onClick={(e) => {
                      e.stopPropagation();
                      setEditingId(chat._id);
                      setEditFirstName(chat.firstName);
                      setEditLastName(chat.lastName);
                    }}>✏️</button>
                    <button onClick={(e) => {
                      e.stopPropagation();
                      handleDelete(chat._id);
                    }}>🗑️</button>
                  </div>
                </div>
              )}
            </li>
          );
        })}
      </ul>
    </div>
  );
}

export default Sidebar;
