import { useState } from 'react';
import Sidebar from './components/Sidebar';
import ChatWindow from './components/ChatWindow';
import './styles/chat.css';

function App() {
    const [selectedChat, setSelectedChat] = useState(null);
    const [user, setUser] = useState(null); // 🟡 Додали user state тут

    return (
        <div className="app">
            <aside className="sidebar-container">
                {/* 🟡 Передаємо setUser і user у Sidebar */}
                <Sidebar onSelect={setSelectedChat} user={user} setUser={setUser} />
            </aside>

            <main className="chat-main">
                {selectedChat ? (
                    <>
                        <header className="chat-header">
                            {selectedChat.firstName} {selectedChat.lastName}
                        </header>
                        {/* 🟡 Передаємо user у ChatWindow */}
                        <ChatWindow chatId={selectedChat._id} user={user} />
                    </>
                ) : (
                    <header className="chat-header">Select a chat to start messaging</header>
                )}
            </main>
        </div>
    );
}

export default App;
